import axios from 'axios';
import React, { useState, useEffect } from 'react'
import { Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function ShowUser() {
    const [users, setData] = useState([]);

    useEffect(() => {
        loaddata();
    }, [])

    const deleteVal =async id => {
        alert(id);
        await axios.delete(`http://localhost:3002/users/${id}`);
        loaddata();
    }
    const loaddata = async () => {
        const getData = await axios.get("http://localhost:3002/users");
        setData(getData.data.reverse());
    }
    return (
        <div className="container">
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Email</th>
                        <th>Password</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((item, i) =>
                        <tr key={i}>
                            <td>{i + 1}</td>
                            <td>{item.fname}</td>
                            <td>{item.lname}</td>
                            <td>{item.email}</td>
                            <td>{item.password}</td>
                            {/* <td><Link className="btn btn-danger" onClick={() => deleteVal(item.id)}>Delete</Link></td> */}
                            <td> <button  className="btn btn-danger" onClick={() => deleteVal(item.id)}>Delete</button></td>
                            <td><Link className="btn btn-info" to={"/editUser/" + item.id}>Edit</Link></td>

                        </tr>
                    )}
                </tbody>
            </Table>
        </div>
    )
}
