import axios from 'axios';
import React, { useState, useEffect } from 'react'
import { Table } from 'react-bootstrap';
import { Link } from 'react-router-dom';

export default function ShowUser() {
    const [users, setData] = useState([]);

    useEffect(() => {
        loaddata();
    }, [])

    const loaddata = async () => {
        const getData = await axios.get("http://localhost:3002/users");
        setData(getData.data.reverse());
    }
    return (
        <div className="container">
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>#</th>
                        <th>Username</th>
                        <th>Email</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((item, i) =>
                        <tr key={i}>
                            <td>{i+1}</td>
                            <td>{item.name}</td>
                            <td>{item.email}</td>
                            <td><Link className="btn btn-danger" >Delete</Link></td>
                            <td><Link className="btn btn-info" to="/editUser">Edit</Link></td>
                            <td><Link to={"/editUser/"+item.id}>Edit</Link></td>
                        </tr>
                    )}
 </tbody>
            </Table>
        </div>
    )
}
