import React,{useState,useEffect} from 'react';
import axios from 'axios';
import { useHistory } from 'react-router';

export default function Login() {
    let history=useHistory()
    const[email,setEmail]=useState('');
    const[pass,setPass]=useState('');

    useEffect(()=>{
        // loaddata();
    },[])
   const submitValues=()=>{

    }
const loaddata = async e => { 
    e.preventDefault();
    const getData = await axios.get("http://localhost:3000/users");
    console.log(getData.data);
    let arr=getData.data;
    arr.find(item=>{
        if(item.email==email&&item.password==pass){
            history.push("/showuser");
        }
        else console.log("username and password wrong");
        
    }
        )
   
}
    return (
        <div className="container">
            <div className="w-50 mx-auto shadow p-5 mt-4">
                <h2 className="text-center mb-4">Login Form</h2>
                <form onSubmit={e=>loaddata(e)}>
                    <div className="form-group mb-2">
                        <input name="email" value={email}  onChange={(e)=>setEmail(e.target.value)} type="text" className="form-control" placeholder="Enter Your Email" />
                    </div>
                    <div className="form-group mb-2">
                        <input name="password" value={pass} onChange={(e)=>setPass(e.target.value)} type="password" className="form-control" placeholder="Enter Your Password" />
                    </div>
                    <button className="btn btn-primary btn-block">Login</button>

                </form>
            </div>
        </div>
    )
}
